/*---------------------------------------------------------------------------
                                Description

    This interface file provides the structure and function declarations
    needed to use the FlashFX Event Manager.
---------------------------------------------------------------------------*/
/*---------------------------------------------------------------------------
                                Revision History
    $Log: eventmgr.h $
    Revision 1.6  2009/01/28 04:54:28Z  glenns
    - Reorganized header file inclusion to relieve some obfuscation
      difficulty.
    Revision 1.5  2009/01/27 20:58:01Z  glenns
    - Changed DECLARE_EVENT_OBJECT macro to eliminate complile
      warning generated by gnu.
    Revision 1.4  2009/01/27 04:41:47Z  glenns
    - Improved interfaces to meet Datalight coding standards.
    - Added union-based data type for event metadata, eliminating
      need for void pointer typecasts in function implementations.
    - Removed "AcquireEvent" and "FreeEvent" interfaces. Clients
      must now declare their own event objects.
    - Added macro to aid in declaring an event object.
    Revision 1.3  2009/01/20 21:51:22Z  glenns
    - Added revision history block
---------------------------------------------------------------------------*/

#ifndef EVENTMGR_H_INCLUDED
#define EVENTMGR_H_INCLUDED

#define FFX_USE_EVENT_MANAGER 1

typedef enum _e_FfxEventType
{
    FFX_EVENT_REPORT_FLASHERROR = 0,
    FFX_EVENT_REPORT_READ,
    FFX_EVENT_REPORT_WRITE,
    FFX_EVENT_REPORT_ERASE,
    FFX_EVENT_REPORT_SUSPEND,
    FFX_EVENT_REPORT_RESUME,
    FFX_EVENT_REQUEST_BLOCKSTATS,
    FFX_EVENT_REQUEST_PAGESTATS,
    FFX_EVENT_NUMEVENTS
} FFX_EVENTTYPE;

struct tagEventMgrInstance;
typedef struct tagEventMgrInstance EVENTMGRINSTANCE;

typedef struct _s_FlashErrorMetadata
{
    D_UINT32 ulFlashErrorType;
    D_UINT32 ulNumBitErrors;
    D_UINT32 ulMaxCorrectible;
    D_UINT32 ulPageOffset;
    D_UINT32 ulBlockNum;
} FLASH_ERROR_METADATA;

typedef struct _s_ReadMetadata
{
    D_UINT32 ulPageOffset;
    D_UINT32 ulBlockNum;
} READ_METADATA;

typedef struct _s_WriteMetadata
{
    D_UINT32 ulPageOffset;
    D_UINT32 ulBlockNum;
} WRITE_METADATA;

typedef struct _s_EraseMetadata
{
    D_UINT32 ulBlockNum;
} ERASE_METADATA;

typedef struct _s_SuspendMetadata
{
    D_UINT32 ulBlockNum;
} SUSPEND_METADATA;

typedef struct _s_ResumeMetadata
{
    D_UINT32 ulBlockNum;
} RESUME_METADATA;

typedef struct _s_BlockStatsMetadata
{
    D_UINT32 ulNumReadsSinceErase;
    D_UINT32 ulNumWritesSinceErase;
    D_UINT32 ulNumErases;
    D_UINT32 ulNumCorrectibleBits;
    D_UINT32 ulNumUncorrectible;
} BLOCKSTATS_METADATA;

typedef struct _s_PageStatsMetadata
{
    D_UINT32 ulNumReadsSinceErase;
    D_UINT32 ulNumWrites;
    D_UINT32 ulNumErases;
    D_UINT32 ulNumCorrectibleBits;
    D_UINT32 ulNumUncorrectible;
} PAGESTATS_METADATA;

typedef union _u_Metadata
{
    FLASH_ERROR_METADATA error_metadata;
    READ_METADATA        read_metadata;
    WRITE_METADATA       write_metadata;
    ERASE_METADATA       erase_metadata;
    SUSPEND_METADATA     suspend_metadata;
    RESUME_METADATA      resume_metadata;
    BLOCKSTATS_METADATA  blockstats_metadata;
    PAGESTATS_METADATA   pagestats_metadata;
} FFX_METADATA;

typedef struct _s_FfxEvent
{
    FFX_EVENTTYPE event;
    FFX_METADATA event_metadata;
} FFX_EVENT;

typedef void (*pfvEventHandler)(FFX_EVENT *);

D_BOOL FfxEventMgrRegisterEventHandler(
    EVENTMGRINSTANCE *pInstance,
    FFX_EVENTTYPE eventtype,
    pfvEventHandler pfvHandler);

D_BOOL FfxEventMgrUnregisterEventHandler(
    EVENTMGRINSTANCE *instance,
    FFX_EVENTTYPE eventtype,
    pfvEventHandler pfvHandler);

void FfxEventMgrPostEvent(EVENTMGRINSTANCE *instance, FFX_EVENT *event);

EVENTMGRINSTANCE *FfxEventMgrCreate(void);

void FfxEventMgrDestroy(EVENTMGRINSTANCE *instance);

#define DECLARE_EVENT_OBJECT(name, type) FFX_EVENT name = {type}

#endif /* EVENTMGR_H_INCLUDED */
