#include <flashfx.h>
#include <fxdriver.h>
#include <vbf.h>
#include <toolfw.h>
#include <oecommon.h>
#include <dlerrlev.h>

#define BUFFLEN 256

extern FFXDRIVERINFO *pDI;


/*-------------------------------------------------------------------
    Public: FfxToolFramework

    Parameters:

    Return:
-------------------------------------------------------------------*/
int FfxToolFramework(
    FFXTOOLPARAMS  *pTP,
    PFNTOOL         pfnTool)
{
    int             iReturn;
    D_UINT16        uArgCount = 0;
    D_UINT16        uDiskNum = 0;
    char            achTempBuff[32];
    char            achBuffer[BUFFLEN];
    FFXDISKHANDLE   hDisk;

    if(pTP->dtp.pszCmdLine)
    {
        DclStrCpy(achBuffer, pTP->dtp.pszCmdLine);
    }
    else
    {
        achBuffer[0] = 0;
    }

    pTP->dtp.pszCmdLine = achBuffer;

    pTP->pszDriveForms = FfxGetDriveFormatString();

    uArgCount = DclArgCount(achBuffer);

    if(uArgCount)
    {
        if(DclArgRetrieve(achBuffer, 1, sizeof(achTempBuff), achTempBuff))
        {
            if(!DclStrCmp(achTempBuff, "?") || !DclStrCmp(achTempBuff, "/?") ||
               !DclStrCmp(achTempBuff, "/h") || !DclStrCmp(achTempBuff, "/H"))
            {
                goto DisplayHelp;
            }

            /*  Retrieve the disk number
            */
            if(!FfxParseDriveNumber(achTempBuff, &uDiskNum, 0))
            {
                DclPrintf("%s: Unable to determine the disk number from '%s'.\n",
                     pTP->dtp.pszCmdName, (const char *) achTempBuff);

                DclPrintf("The disk must be specified in the form: %s\n\n", pTP->pszDriveForms);

                return DCLERRORLEVEL_INVALIDDEVICE;
            }

            /*  clear the drive designation parameter
            */
            DclArgClear(achBuffer, 1);
        }
        else
        {
            goto DisplayHelp;
        }

        if(!FfxDriverLock(pDI))
        {
            DclPrintf("%s: Unable to lock device driver\n", pTP->dtp.pszCmdName);

            return DCLERRORLEVEL_DRIVERLOCKFAILED;
        }

        /*  Get the FlashFX disk handle.
        */
        hDisk = FfxDriverDiskHandle(pDI, (unsigned)uDiskNum);
        if(hDisk)
        {
            pTP->nDiskNum = (*hDisk)->Conf.nDiskNum;
            pTP->nDeviceNum = (*hDisk)->Conf.DiskSettings.nDevNum;
            iReturn = (*pfnTool) (pTP);
        }
        else
        {
            iReturn = DCLERRORLEVEL_INVALIDDEVICE;
        }

        FfxDriverUnlock(pDI);

        return iReturn;
    }

  DisplayHelp:

    DclStrCpy(achBuffer, "/?");

    return (*pfnTool) (pTP);
}
